import React from 'react';
import './App.css';
import Footer from './components/Footer'
import Header from './components/Header'
import MainContent from './MainContent'

function App() {
  return (
    <div className="App">
      <Header />
      <MainContent />
  <h1 style={{color:"red"}}>Good {Greet()}</h1>
      <Footer />
    </div>
  );
}

function Greet(){
  const date = new Date()
  const hours = date.getHours()
  let timeOfDay
  if (hours < 12){
    timeOfDay = "morning"
  }else if(hours >= 12 && hours <17){
    timeOfDay = 'afternoon'
  }else{
    timeOfDay = "night"
  }
  return(
    timeOfDay
  )
}

export default App;
