import React from 'react';
import './App.css';
import Footer from './components/Footer'
import Header from './components/Header'
//import MainContent from './MainContent'
import Contactcard from './components/Contactcard';
import photosData from './components/photosData'

function getPhotos(method, url){
  return new Promise(function(resolve, reject){
    let photos = new XMLHttpRequest()
    photos.open(method, url)
    photos.send();
    console.log(photos.response)
  })

}


class App extends React.Component {

constructor(){
  super()
  this.state = {
    isFriendly: true,
    loading: false,
    character : {},
    fName: "",
    lName: ""
  }
  this.handleClick = this.handleClick.bind(this)
  this.handleChange = this.handleChange.bind(this)
}

handleClick(){
  this.setState(prevState => {
      return{
        isLoggedin : !prevState.isLoggedin
      }
  })
}

handleChange(event){
  const {name, value, type, checked} = event.target
  type === "checkbox" ? this.setState({[name]:checked}): this.setState({[name]: value}) 

}

componentDidMount(){
  this.setState({
    loading: true
  })
  fetch("https://swapi.dev/api/people/1")
  .then(response => response.json())
  .then(data => this.setState({
    character: data,
    loading: false
  }))
}

 render(){
  const jokeComponents = photosData.map(photo => <Contactcard key = {photo.id} id = {photo.id} imgTitle={photo.title} imgUrl = {photo.url} albumId = {photo.albumId} thumbnail = {photo.thumbnail} />)
  let wordDisplay
  if (this.state.isLoggedin){
    wordDisplay = "in"
  }else{
    wordDisplay = 'out'
  }

  let buttonText = this.state.isLoggedin ? "Log Out" : "Log In"
  let displayText = this.state.isLoggedin ? "Loged In" : "Loged Out YOOOcd .."
  let people = this.state.loading ? "Loading..." : this.state.character.name
  return ( 
    <form className="App">     
      <Header />
      <input type="text" placeholder="First Name" name="fName" value={this.state.fName } onChange={this.handleChange}></input>
      <br />
      <input type="text" placeholder="Last Name" name="lName" value={this.state.lName } onChange={this.handleChange}></input>
      <br />
      <textarea value="Some default value" />
      <br />
      <input type = "checkbox" name = "isFriendly" checked = {this.state.isFriendly} onChange={this.handleChange} />
  <h2>{people} {this.state.fName} {this.state.lName}</h2>
      <button onClick={this.handleClick}>{buttonText}</button>
      <h3>{displayText}</h3>
      <h1>{this.state.count} </h1>
      <h1 style={styles}>Good Morning!</h1>
      <Footer />
    </form>
  );
}
}

const styles = {
  color:"red",
  backgroundColor:"cyan",
  fontSize: 100
}

function Greet(){
  const date = new Date()
  const hours = date.getHours()
  let timeOfDay
  if (hours < 12){
    timeOfDay = "morning"
    styles.color = 'red'
  }else if(hours >= 12 && hours <17){
    timeOfDay = 'afternoon'
    styles.color = 'purple'
  }else{
    timeOfDay = "night"
    styles.color = 'blue'
  }
  return(
    timeOfDay
  )
}

export default App;
