import React from 'react';
import './App.css';
import Footer from './components/Footer'
import Header from './components/Header'
import MainContent from './MainContent'
import Contactcard from './components/Contactcard';


function App() {

  return (
    <div className="App">
       <Contactcard 
       contact = {{name: 'Ashu', imgUrl: 'https://img.webmd.com/dtmcms/live/webmd/consumer_assets/site_images/article_thumbnails/other/cat_relaxing_on_patio_other/1800x1200_cat_relaxing_on_patio_other.jpg?resize=750px:*', phone:'7012263447', email: 'sng11asu@gmail.com' }}
       />
       <Contactcard 
       name = 'Ashutosh Singh'
       imgUrl= "https://img.webmd.com/dtmcms/live/webmd/consumer_assets/site_images/article_thumbnails/other/cat_relaxing_on_patio_other/1800x1200_cat_relaxing_on_patio_other.jpg?resize=750px:*"
       phone = "7012263457"
       email = "sng11asu@gmail.com"
       />
      <Header />
      <MainContent />
  <h1 style={styles}>Good {Greet()}</h1>
      <Footer />
    </div>
  );
}

const styles = {
  color:"red",
  backgroundColor:"cyan",
  fontSize: 100
}

function Greet(){
  const date = new Date()
  const hours = date.getHours()
  let timeOfDay
  if (hours < 12){
    timeOfDay = "morning"
    styles.color = 'red'
  }else if(hours >= 12 && hours <17){
    timeOfDay = 'afternoon'
    styles.color = 'purple'
  }else{
    timeOfDay = "night"
    styles.color = 'blue'
  }
  return(
    timeOfDay
  )
}

export default App;
