import React from 'react';
import './App.css';
import Footer from './components/Footer'
import Header from './components/Header'
import MainContent from './MainContent'


function App() {

  return (
    <div className="App">
      <Header />
      <MainContent />
  <h1 style={styles}>Good {Greet()}</h1>
      <Footer />
    </div>
  );
}

const styles = {
  color:"red",
  backgroundColor:"cyan",
  fontSize: 100
}

function Greet(){
  const date = new Date()
  const hours = date.getHours()
  let timeOfDay
  if (hours < 12){
    timeOfDay = "morning"
    styles.color = 'red'
  }else if(hours >= 12 && hours <17){
    timeOfDay = 'afternoon'
    styles.color = 'purple'
  }else{
    timeOfDay = "night"
    styles.color = 'blue'
  }
  return(
    timeOfDay
  )
}

export default App;
