  return (
    <div className="App">
      <Header count={this.state.isLoading} />
      <button onClick={this.handleClick}>Click me</button>
      <h1>{this.state.count} </h1>
      {jokeComponents}
      <h1 style={styles}>Good Morning!</h1>
      <Footer />
    </div>
  );
}
}

const styles = {
  color:"red",
  backgroundColor:"cyan",
  fontSize: 100
}

function Greet(){
  const date = new Date()
  const hours = date.getHours()
  let timeOfDay
  if (hours < 12){
    timeOfDay = "morning"
    styles.color = 'red'
  }else if(hours >= 12 && hours <17){
    timeOfDay = 'afternoon'
    styles.color = 'purple'
  }else{
    timeOfDay = "night"
    styles.color = 'blue'
  }
  return(
    timeOfDay
  )
}

export default App;
