import React from 'react';
import './App.css';
import Footer from './components/Footer'
import Header from './components/Header'
//import MainContent from './MainContent'
import Contactcard from './components/Contactcard';
import photosData from './components/photosData'

function getPhotos(method, url){
  return new Promise(function(resolve, reject){
    let photos = new XMLHttpRequest()
    photos.open(method, url)
    photos.send();
    console.log(photos.response)
  })

}


class App extends React.Component {

constructor(){
  super()
  this.state = {
    isLoggedin: true
  }
}
 render(){
  const jokeComponents = photosData.map(photo => <Contactcard key = {photo.id} id = {photo.id} imgTitle={photo.title} imgUrl = {photo.url} albumId = {photo.albumId} thumbnail = {photo.thumbnail} />)
  let wordDisplay
  if (this.state.isLoggedin){
    wordDisplay = "in"
  }else{
    wordDisplay = 'out'
  }

  return (
    <div className="App">
      <Header answer={this.state.answer} />
      <h1>Is this state important to know? {wordDisplay} </h1>
      {jokeComponents}
      <h1 style={styles}>Good {Greet()}</h1>
      <Footer />
    </div>
  );
}
}

const styles = {
  color:"red",
  backgroundColor:"cyan",
  fontSize: 100
}

function Greet(){
  const date = new Date()
  const hours = date.getHours()
  let timeOfDay
  if (hours < 12){
    timeOfDay = "morning"
    styles.color = 'red'
  }else if(hours >= 12 && hours <17){
    timeOfDay = 'afternoon'
    styles.color = 'purple'
  }else{
    timeOfDay = "night"
    styles.color = 'blue'
  }
  return(
    timeOfDay
  )
}

export default App;
